local Whiteboard = require("whiteboard")

local client = Client(
    arg[1], 
    "allo-whiteboard"
)
local app = App(client)

print("+==================+")
print("+ WHITEBOARD ADDED +")
print("+==================+")


-- CREATES THE WHITEBOARD
local whiteboardView = Whiteboard(ui.Bounds(1.5, 1, 0,   2, 1, 0.1))

-- ADDS THE GRAB HANDLE
local grabHandle = ui.GrabHandle(ui.Bounds(-0.9, -0.5, 0.5,   0.2, 0.2, 0.2))
whiteboardView:addSubview(grabHandle)

-- ADDS THE CLEAR BUTTON
-- TODO: Make the textures into constants to get these silly strings out of the code
local clearButton = ui.Button(ui.Bounds(0, -0.63, 0.0,   0.2, 0.2, 0.2))
clearButton:setDefaultTexture("")
clearButton:setHighlightTexture("")
clearButton:setActivatedTexture("")
clearButton.onActivated = function()
  whiteboardView:clearBoard()
end
whiteboardView:addSubview(clearButton)

-- ADDS BRUSH SIZE DOWN BUTTON
local brushSizeDownButton = ui.Button(ui.Bounds(0.7, -0.63, 0.0,  0.2, 0.2, 0.2) )
brushSizeDownButton:setDefaultTexture("")
brushSizeDownButton:setHighlightTexture("")
brushSizeDownButton:setActivatedTexture("")
brushSizeDownButton.onActivated = function()
  whiteboardView:setBrushSize(whiteboardView.brushSize - 1)
end
whiteboardView:addSubview(brushSizeDownButton)

-- ADDS BRUSH SIZE UP BUTTON
local brushSizeUpButton = ui.Button(ui.Bounds(0.9, -0.63, 0.0,  0.2, 0.2, 0.2) )
brushSizeUpButton:setDefaultTexture("")
brushSizeUpButton:setHighlightTexture("")
brushSizeUpButton:setActivatedTexture("")
brushSizeUpButton.onActivated = function()
  whiteboardView:setBrushSize(whiteboardView.brushSize + 1)
end
whiteboardView:addSubview(brushSizeUpButton)


-- ADDS RESIZE BUTTON
local resizeButton = ui.Button(ui.Bounds(-0.4, -0.63, 0.0,  0.2, 0.2, 0.2) )
resizeButton:setDefaultTexture("")
resizeButton:setHighlightTexture("")
resizeButton:setActivatedTexture("")
resizeButton.onActivated = function()
  whiteboardView:resize(ui.Bounds(1.5, 1, 0,   3, 2, 0.1))
end
whiteboardView:addSubview(resizeButton)

app.mainView = whiteboardView

-- Checks whiteboard refresh 10 times per second
app:scheduleAction(0.1, true, function() 
  whiteboardView:sendIfDirty()
end)

app:connect()
app:run()
